void* mymalloc(int size,char* file,int line)
{
	int i=0;
	int tagSize;
	int location=0;
	int temp;
	int available=0;
	int spaceForTag;
	if(size==0)
	{
		return NULL;
	}else if(size<0||size>4094)
	{
		printf("Malloc error in file %s on line %d:\nMalloc expects a size between 0 and 4094 but found a size of %d\n\n",file,line,size);
		return NULL;
	}else{
		if(size<=128)
		{
			spaceForTag=1;
		}else
		{
			spaceForTag=2;
		}
		for(i=0;i<4096;i++)
		{
			if(myblock[i])
			{
				tagSize=getTagSize(myblock+i);
				if(tagSize==1)
				{
					temp=i+interpretOneByteTag(myblock+i);
					i=temp;
				}else
				{
					temp=i+interpretTwoByteTag(myblock+i);
					i=temp+1;
				}
				location=i+1;
				available=0;
			}else
			{
				available++;
			}
			if(available==(size+spaceForTag))
			{
				if(spaceForTag==1)
				{
					makeOneByteTag(myblock+location,size);
					return (void*)(myblock+location+1);
				}else
				{
					makeTwoByteTag(myblock+location,size);
					return (void*)(myblock+location+2);
				}
			}	
		}
		printf("Malloc error in file %s on line %d:\nNot enough space available for the amount of myblock requested\n\n",file,line);
		return NULL;
	}
}
void myfree(void* target,char* file,int line)
{
	if(target<(void*)myblock||target>(void*)(myblock+4095))
	{
		printf("Free error in file %s on line %d:\nAdress atempted to be freed is not an adress managed by Malloc\n\n",file,line);
		return;
	}else
	{
		int i;
		int temp;
		int tagSize;
		for(i=0;i<4096;i++)
		{
			if(target==(void*)(myblock+i))
			{
				printf("Free error in file %s on line %d:\nAdress atempted to be freed is not an adress that is currently Malloc'd\n\n",file,line);
				return;
			}else if(myblock[i])
			{
				tagSize=getTagSize(myblock+i);
				if(target==(void*)(myblock+i+tagSize))
				{
					clear(myblock+i,tagSize);
					return;
				}else
				{
					if(tagSize==1)
					{
						temp=i+interpretOneByteTag(myblock+i);
						i=temp;
					}else
					{
						temp=i+interpretTwoByteTag(myblock+i);
						i=temp+1;
					}
					if((void*)(myblock+i)>=target)
					{
						printf("Free error in file %s on line %d:\nAdress atempted to be freed has been Malloc'd but is not the start of a Malloc'd region\n\n",file,line);
						return;
					}
				}
			}
		}
	}
	printf("Error in Free:\nYou shouldn't be reading this\n");
	return;
}
void clear(char* start,int tagSize)
{
	int end;
	int i;
	if(tagSize==1)
	{
		end=interpretOneByteTag(start)+tagSize;
	}else
	{
		end=interpretTwoByteTag(start)+tagSize;
	}
	for(i=0;i<end;i++)
	{
		start[i]=0;
	}
	return;
}
int getTagSize(char* target)
{
	unsigned char byte=*target;
	if(byte>127)
	{
		return 1;
	}else
	{
		return 2;
	}
}
int interpretOneByteTag(char* target)
{
	unsigned char byte=*target;
	byte=byte-127;
	return (int)byte;
}
int interpretTwoByteTag(char* target)
{
	unsigned char firstByte=target[0];
	unsigned char secondByte=target[1];
	int result=(int)secondByte;
	result=result+((int)firstByte*128);
	return result;
}
void makeOneByteTag(char* target,int size)
{
	unsigned char byte=(unsigned char)size;
	byte=byte+127;
	*target=byte;
	return;
}
void makeTwoByteTag(char* target,int size)
{
	int secondInt=size%128;
	unsigned char secondByte=(unsigned char)secondInt;
	int firstInt=size-secondInt;
	firstInt=firstInt/128;
	unsigned char firstByte=(unsigned char)firstInt;
	target[0]=firstByte;
	target[1]=secondByte;
	return;
}
